import { useConnect, SignInButton } from 'ethos-wallet-beta'

function App() {
  const { isConnected, isLoading, error } = useConnect()

  return (
    <>
        {
            isLoading ? (
                <div>Loading...</div>
            ) : isConnected ? (
                <div>
                    Wallet connected
                    <TheRestOfTheApp />
                </div>
                ) : (
                <div>
                    No wallet connected
                    <SignInButton />
                </div>
            )
        }

        {error && <div>{error.message}</div>}
    </>
  )
}

import { useWalletContents } from 'ethos-wallet-beta'

function App() {
  const { suiBalance, coins, nfts } = useWalletContents()

  return (
    <>
      <div>
          Balance: {suiBalance}
      </div>
      <div>
          {coins.map(coin => {
            return <span>{coin.name}: {coin.balance}</span>
          })}
      </div>
      <div>
          {nfts.map(nft => {
            return (
              <img
                src={nft.src}
                alt={nft.name}
              />
            )
          })}
      </div>
    </>
  )
}
